CREATE TABLE "user_shop"
(
    id      SERIAL PRIMARY KEY,
    deposit INT,
    login   VARCHAR(255) NOT NULL
);

CREATE TABLE basket (
                        id SERIAL PRIMARY KEY,
                        user_id INT REFERENCES "user_shop"(id),
                        product_id INT,
                        count INT NOT NULL
);

CREATE TABLE product (
                         id SERIAL PRIMARY KEY,
                         name VARCHAR(255) NOT NULL,
                         cost INT NOT NULL
);

